body.script:

$(document).ready(function(){  

  $('#gameBtn').click(function(){  
      $.ajax({                        
          cache: false,  
          success: function(html){  
              $("#content").html(gameTmpl());  
          }  
      });  
  });
  $('#autorizeBtn').click(function(){  
      $.ajax({  
          cache: false,  
          success: function(html){  
              $("#content").html(loginTmpl());  
          }  
      });  
  });  
  $('#scoreBtn').click(function(){  
      $.ajax({   
          cache: false,  
          success: function(html){  
              $("#content").html(scoreboardTmpl());  
          }  
      });  
  });    
})
